import React, { useEffect } from 'react';
import axios from 'axios';
import baseURL from '../../utils/baseURL';

const Categories = () => {
  useEffect(() => {
    const getCategories = async () => {
      try {
        const res = await axios.get(`${baseURL}/api/categoy`);
        console.log(res);
      } catch (error) {
        console.log(error);
      }
    };
    getCategories();
  }, []);

  return (
    <div>
      <div className="category-container">
        <div className="list">
          <div
            className="category-img"
            style={{
              backgroundImage: `url(https://colorlib.com/preview/theme/cozastore/images/banner-04.jpg)`
            }}
          ></div>
          <div className="name">MEN</div>
        </div>
        <div className="list">
          <div
            className="category-img"
            style={{
              backgroundImage: `url(https://colorlib.com/preview/theme/cozastore/images/banner-05.jpg)`
            }}
          ></div>
          <div className="name">WOMEN</div>
        </div>
      </div>
      <style jsx>{`
        .category-container {
          padding: 1rem 2rem 3rem 1rem;
          display: grid;
          grid-template-columns: 1fr 1fr;
          grid-gap: 2rem;
        }

        .list {
          position: relative;
        }

        .name {
          position: absolute;
          top: 3rem;
          left: 3rem;
          font-size: 3rem;
          font-weight: 600;
          text-transform: uppercase;
        }

        .category-img {
          width: 100%;
          height: 100%;
          padding-top: 56.25%;
          background-size: cover;
          border: 1px solid #eee;
        }
      `}</style>
    </div>
  );
};

export default Categories;
