import { FaCheck } from 'react-icons/fa';
import PropTypes from 'prop-types';

const Popup = ({ message }) => {
  return (
    <div className="popup-container">
      <div className="message"> Successfully added to cart </div>
      <FaCheck color={'#59981A'} size={64} />
      <style jsx>{`
        .popup-container {
          position: fixed;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          height: 300px;
          width: 300px;
          background: rgba(0, 0, 0, 0.8);
          z-index: 10;
          border-radius: 15px;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          padding: 1rem;
        }

        .message {
          color: #fff;
          font-size: 2rem;
          font-weight: 600;
          padding: 1rem;
        }
      `}</style>
    </div>
  );
};

Popup.propTypes = {
  message: PropTypes.string
};

export default Popup;
