import connectDB from "../../utils/connectDB";

connectDB();

export default async (req, res) => {
  switch (req.method) {
    case "GET":
      await handleGetRequest(req, res);
      break;
    case "PUT":
      break;
    default:
      res.status(405).send(`Method not allowed`);
  }
};

function handleGetRequest(req, res) {
  console.log("test");
}
