import ReactDOM from 'react-dom';

const Modal = ({ children, isOpen }) =>
  isOpen
    ? ReactDOM.createPortal(
        <div className="modal">
          <div className="modal-body">{children}</div>
          <style jsx>{`
            .modal {
              position: fixed;
              top: 0;
              left: 0;
              right: 0;
              z-index: 99;
              background: rgba(0, 0, 0, 0.5);
            }
          `}</style>
        </div>,
        document.body
      )
    : null;

export default Modal;
