import React, { useState, useEffect } from 'react';
import { IoMdAdd } from 'react-icons/io';
import { useModal } from '../../store';
import { useToast } from '../../hooks';
import { Modal, Toast } from '../../components/Shared';
import Layout from '../../components/Layout';
import ProductForm from '../../components/Admin/Home/ProductForm';
import ProductTable from '../../components/Admin/Home/ProductTable';
import axios from 'axios';
import baseURL from '../../utils/baseURL';

const Admin = () => {
  const initState = {
    name: '',
    price: '',
    description: '',
    category: ''
  };

  const { show, closeModal, openModal } = useModal();
  const { isOpen, showToast } = useToast();
  const [message, setMessage] = useState('');
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState(initState);
  const [imagePreview, setImagePreview] = useState('');
  const [file, setFile] = useState('');
  const [submit, setSubmit] = useState(false);

  useEffect(() => {
    async function getProducts() {
      try {
        const { data } = await axios.get(`${baseURL}/api/products`);
        setProducts(data.products);
      } catch (error) {
        console.log(error);
      }
    }
    getProducts();
  }, []);

  const handleChange = e => {
    if (e.target.name === 'file') {
      let reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(event.target.files[0]);
      setFile(e.target.files[0]);
    } else {
      setProduct({
        ...product,
        [e.target.name]: e.target.value
      });
    }
  };

  const imageUpload = async () => {
    try {
      let data = new FormData();
      data.append('file', file);
      data.append('upload_preset', 'dress-shop');
      data.append('cloud_name', 'djlbfjouc');
      //upload image to cloudinary
      const res = await axios.post(process.env.CLOUDINARY_URL, data);
      return res.data.secure_url;
    } catch (error) {
      console.log(error.response);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      setSubmit(true);
      //get url of image
      const imageURL = await imageUpload();
      const payload = { ...product, imageURL };
      //save product to db
      await axios.post(`${baseURL}/api/products`, payload);
      reset();
    } catch (error) {
      console.log(error.response);
    } finally {
      setSubmit(false);
    }
  };

  const reset = () => {
    setProduct(initState);
    setFile('');
    setImagePreview('');
  };

  async function handleDeleteProduct(id) {
    try {
      const payload = { params: { id } };
      await axios.delete(`${baseURL}/api/product`, payload);
      const filteredProducts = products.filter(product => product._id !== id);
      setProducts(filteredProducts);
    } catch (error) {
      console.log(error);
    }
  }

  function addProductFormModal() {
    return (
      <Modal show={show} close={closeModal} title={`Add Product`}>
        <ProductForm
          handleSubmit={handleSubmit}
          handleChange={handleChange}
          submit={submit}
          product={product}
          imagePreview={imagePreview}
        />
      </Modal>
    );
  }

  function confirmDeleteModal(id) {
    return (
      <Modal
        show={show}
        close={closeModal}
        title={`Delete Product`}
        hasFooter
        onSubmit={async () => {
          await handleDeleteProduct(id);
          showToast();
          setMessage('Successfully Deleted');
        }}
      >
        <div style={{ fontSize: '2rem' }}>
          Are you sure you want to delete this product?
        </div>
      </Modal>
    );
  }

  return (
    <Layout>
      <div className="container">
        <Toast isOpen={isOpen} message={message} />
        <div className="welcome-text">Hi, Welcome Back Admin</div>
        <div className="header">
          <div className="page-title"> List of All Products </div>
          <button
            onClick={() => openModal(addProductFormModal)}
            className="btn"
          >
            <span className="btn-text"> Add Product</span>
            <IoMdAdd />
          </button>
        </div>
        <ProductTable
          products={products}
          deleteProduct={id => openModal(confirmDeleteModal(id))}
        />
      </div>{' '}
      <style jsx>{`
        .container {
          max-width: 120rem;
          margin: 3rem auto;
          padding: 0 2rem;
        }
        .page-title {
          font-size: 2.5rem;
        }

        .header {
          display: flex;
          justify-content: space-between;
        }

        .welcome-text {
          font-size: 2rem;
          padding-bottom: 2rem;
        }

        .btn {
          height: 5rem;
          padding: 0 2rem;
          background-color: var(--color-dark);
          color: #fff;
          font-size: 2rem;
          border: 1px solid transparent;
          cursor: pointer;
        }

        .btn-text {
          margin-right: 0.5rem;
        }
      `}</style>{' '}
    </Layout>
  );
};

export default Admin;
