import React, { useEffect, useState } from "react";
import axios from "axios";
import Layout from "../components/Layout";
import baseURL from "../utils/baseURL";
import Banner from "../components/Home/Banner";
import ProductList from "../components/Shared/Products/ProductList";

const Home = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [products, setProducts] = useState([]);
  const [isLoadMore, setIsLoadMOre] = useState(true);

  useEffect(() => {
    async function fetchProducts() {
      const payload = { params: { currentPage, pageSize: 10 } };
      const { data } = await axios.get(`${baseURL}/api/products`, payload);
      const newProducts = products.concat(data.products);
      setProducts(products.concat(data.products));
      //check if theres still a products to show
      if (newProducts.length >= data.totalProducts) setIsLoadMOre(false);
    }
    fetchProducts();
  }, [currentPage]);

  const handleLoadMore = () => {
    setCurrentPage(page => page + 1);
  };

  return (
    <>
      <Layout>
        <Banner />
        <div className="container">
          <div className="heading">Product Overview</div>
          <ProductList products={products} />
          {isLoadMore && (
            <div className="load-more">
              <button className="btn" onClick={handleLoadMore}>
                {" "}
                Load More{" "}
              </button>
            </div>
          )}
        </div>
      </Layout>
      <style jsx>
        {`
          .container {
            max-width: 1200px;
            margin: 2rem auto;
          }

          .heading {
            padding: 1.5rem;
            font-weight: 700;
            font-size: 36px;
            line-height: 1.1;
            text-transform: uppercase;
          }

          .load-more {
            width: 100%;
            display: flex;
            justify-content: center;
            margin: 5rem 0;
          }

          .load-more .btn {
            background-color: #fff;
            color: var(--color-primary);
            font-size: 2rem;
            width: 25rem;
            height: 6rem;
            line-height 6rem;
            border-radius: 5rem;
            border: 1px solid var(--color-primary);
            cursor: pointer;
          }

          .load-more .btn:focus {
            outline: none;
          }

          .load-more .btn:hover {
            background-color: var(--color-primary);
            color: #fff;
          }
        `}
      </style>
    </>
  );
};

export default Home;
