import React, { useState } from 'react';
import Layout from '../../components/Layout';
import useModal from '../../hooks/useModal';
import Modal from '../../components/Shared/Modal';
import ProductForm from '../../components/Admin/AddProduct/ProductForm';

const Admin = () => {
  const { show, closeModal, openModal } = useModal();

  return (
    <Layout>
      <div className="container">
        <Modal isOpen={show} close={closeModal} title={`Add Product`}>
          <ProductForm />
        </Modal>
        <div className="welcome-text">Hi, Welcome Back Admin</div>
        <div className="header">
          <div className="page-title"> List of All Products </div>
          <button onClick={openModal} className="btn">
            {' '}
            Add Product
          </button>
        </div>
      </div>{' '}
      <style jsx>{`
        .container {
          max-width: 120rem;
          margin: 3rem auto;
          padding: 0 2rem;
        }
        .page-title {
          font-size: 2rem;
        }

        .header {
          display: flex;
          justify-content: space-between;
        }

        .welcome-text {
          font-size: 1.8rem;
          padding-bottom: 1rem;
        }

        .btn {
          height: 4rem;
          width: 15rem;
          background-color: var(--color-dark);
          color: #fff;
          font-size: 2rem;
          border: 1px solid transparent;
        }
      `}</style>{' '}
    </Layout>
  );
};

export default Admin;
