import React from 'react';

const Pagination = ({ total, limit, activePage }) => {
  return (
    <>
      <ul className="pagination">
        <li>
          <a href="#">1</a>
        </li>
        <li>
          <a href="#">2</a>
        </li>
      </ul>
      <style jsx>{`
        .pagination {
          display: flex;
          padding: 2rem 0;
          align-items: center;
          justify-content: center;
        }

        .pagination li {
          padding: 1rem 2rem;
          font-size: 1.6rem;
          background-color: var(--color-dark);
          color: #fff;
        }

        .pagination li a {
          color: #fff;
        }
      `}</style>
    </>
  );
};

export default Pagination;
