import { IoMdCart } from "react-icons/io";
import { MdAccountCircle } from "react-icons/md";

export default () => {
  return (
    <>
      <header className="header">
        <div className="header-wrapper">
          <div className="site-title">Dress</div>
          <ul className="header-right">
            <li>
              <IoMdCart size={30} />
              <span> Cart </span>
            </li>
            <li>
              <MdAccountCircle size={30} />
              <span> Sign In </span>
            </li>
          </ul>
        </div>
      </header>
      <style jsx>
        {`
          .header {
            position: sticky;
            z-index: 99;
            top: 0;
            left: 0;
            background-color: #fff;
          }
          .site-title {
            font-size: 3rem;
            font-weight: 600;
          }
          .header-wrapper {
            display: flex;
            align-items: center;
            max-width: 120rem;
            margin: 0 auto;
            height: 7rem;
            padding: 0 2rem;
          }

          .header-right {
            flex: 1;
            display: flex;
            justify-content: flex-end;
          }

          .header-right li {
            padding: 0 10px;
            display: flex;
            align-items: center;
            font-size: 1.7rem;
          }

          .header-right span {
            padding-left: 0.6rem;
          }
        `}
      </style>
    </>
  );
};
