import React, { createContext, useReducer, useContext } from "react";
import { ADD_CART, REMOVE_CART } from "./cart.types";
import reducer from "./cart.reducer";
import baseURL from "../../utils/baseURL";
import axios from "axios";

const CartContext = createContext();

const CartProvider = ({ children }) => {
  const initialState = {
    carts: []
  };

  const [state, dispatch] = useReducer(reducer, initialState);

  const addCart = async cart => {
    dispatch({ type: ADD_CART, payload: cart });
    const payload = cart;
    const res = await axios.post(`${baseURL}/api/cart`, payload);
    console.log(res);
  };

  const removeCart = cartId => {
    dispatch({ type: REMOVE_CART, payload: cartId });
  };

  return (
    <CartContext.Provider value={{ carts: state.carts, addCart, removeCart }}>
      {children}
    </CartContext.Provider>
  );
};

const useCart = () => useContext(CartContext);

export { CartProvider, useCart };
