import { useState } from 'react';
import { IoMdCart, IoIosSearch, IoIosClose } from 'react-icons/io';
import { AccountIcon } from '../../components/Shared/Icons';
import Link from 'next/link';
import { useCart } from '../../store/cart/cartContext';
import { useAuth } from '../../store/auth/authContext';
import Router from 'next/router';

export default () => {
  const { carts } = useCart();
  const { currentUser, logout } = useAuth();
  const [isOpenDropdown, setIsOpenDropdown] = useState(false);
  const [isOpenSearch, setIsOpenSearch] = useState(false);
  const [search, setSearch] = useState('');

  const handleSearchSubmit = e => {
    e.preventDefault();
    setIsOpenSearch(false);
    Router.push(`/search?keyword=${search}`);
  };

  return (
    <>
      <header className="header">
        <ul className="header-wrapper">
          <Link href="/">
            <a className="site-title">Dress</a>
          </Link>
          <div className="header-right">
            <div className="desktop-menu header-item">
              <div className="search-container">
                <form className="search-form" onSubmit={handleSearchSubmit}>
                  <input
                    type="text"
                    className="search-input"
                    onChange={e => setSearch(e.target.value)}
                    value={search}
                  />
                  <button className="search-button" type="submit">
                    {' '}
                    <IoIosSearch size={30} />{' '}
                  </button>
                </form>
              </div>
              <li>
                <Link href="/cart">
                  <a>
                    <IoMdCart size={30} />
                    Cart
                    {currentUser && carts.length > 0 && (
                      <div className="cart-num">{carts.length}</div>
                    )}
                  </a>
                </Link>
              </li>
              {currentUser ? (
                <li>
                  <div
                    className="user"
                    onClick={() => setIsOpenDropdown(!isOpenDropdown)}
                  >
                    {' '}
                    {currentUser.name
                      .split(' ')[0]
                      .charAt(0)
                      .toUpperCase()}{' '}
                  </div>
                  {isOpenDropdown && (
                    <ul className="dropdown">
                      <li>
                        <span>{currentUser.name}</span>
                      </li>
                      {currentUser && currentUser.role === 'admin' && (
                        <li>
                          <Link href="/admin">
                            <a> Admin Home </a>
                          </Link>
                        </li>
                      )}
                      <li>
                        <Link href="/order">
                          <a>
                            <span> My orders </span>
                          </a>
                        </Link>
                      </li>
                      <li>
                        <button className="btn" onClick={logout}>
                          LOG OUT
                        </button>
                      </li>
                    </ul>
                  )}
                </li>
              ) : (
                <li>
                  <Link href="/signup">
                    <a>Sign Up</a>
                  </Link>
                  <span style={{ fontWeight: '300', padding: '0 1rem' }}>
                    {' '}
                    |{' '}
                  </span>
                  <Link href="/login">
                    <a>Log In</a>
                  </Link>
                </li>
              )}
            </div>
