const TabCategory = ({ active }) => {
  return (
    <div>
      <ul className="tab">
        <li className={`${active === 'men' ? 'active' : ''}`}>Men</li>
        <li className={`${active === 'women' ? 'active' : ''}`}>Women</li>
      </ul>
      <style>
        {`

        .tab {
            display: flex;
            align-items: center;
        }

        .tab li {
            padding: 1rem 5rem;
            text-align: center;
            font-size: 2.2rem;
            border-bottom: 2px solid transparent;
        }

        .tab .active {
            border-bottom: 2px solid var(--color-primary);
            color: var(--color-primary);
            font-weight: 600;
        }

            `}
      </style>
    </div>
  );
};

export default TabCategory;
