import { MdExpandMore } from 'react-icons/md';

const Filter = () => {
  return (
    <div className="filter-container">
      {' '}
      <span className="label"> Sort by </span>
      <div className="select">
        {' '}
        <span>Price</span>
        <span>
          <MdExpandMore />
        </span>
        <ul className="dropdown">
          <li> Low to High </li>
          <li> High to Low </li>
        </ul>
      </div>
      <style jsx>{`
        .filter-container {
          display: flex;
          align-items: center;
        }

        .label {
          font-size: 1.9rem;
          padding-right: 1rem;
        }

        .select {
          border: 1px solid #eee;
          position: relative;
          font-size: 1.8rem;
          padding: 1rem 2rem;
          width: 20rem;
        }

        .dropdown {
          display: none;
          position: absolute;
        }
      `}</style>
    </div>
  );
};

export default Filter;
