import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import baseURL from "../utils/baseURL";
import axios from "axios";

const SignUp = () => {
  const userObj = {
    email: "",
    name: "",
    password: ""
  };

  const [user, setUser] = useState(userObj);

  function handleChange(e) {
    setUser({ ...user, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    const payload = user;
    try {
      const { data } = await axios.post(`${baseURL}/api/signup`, payload);
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <>
      <Layout>
        <div className="container">
          <form onSubmit={handleSubmit}>
            <div>
              <input
                type="text"
                placeholder="Name"
                name="name"
                value={user.name}
                onChange={handleChange}
              />
            </div>
            <div>
              <input
                type="email"
                placeholder="Email"
                name="email"
                value={user.email}
                onChange={handleChange}
              />
            </div>
            <div>
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={user.password}
                onChange={handleChange}
              />
            </div>
            <div>
              <button type="submit"> Sign Up </button>
            </div>
          </form>
        </div>
      </Layout>
      <style jsx>
        {`
          .container {
            max-width: 1200px;
            margin: 2rem auto;
          }
        `}
      </style>
    </>
  );
};

export default SignUp;
