import { useState } from 'react';

const useModal = modal => {
  const [show, setShow] = useState(false);

  function closeModal() {
    setShow(false);
  }

  function openModal() {
    setShow(true);
  }

  return { show, closeModal, openModal };
};

export default useModal;
