const ProductAction = () => {
  return (
    <>
      <div className="action">
        <div className="qty-container">
          <button className="btn">-</button>
          <input type="text" value="1" className="qty-input" />
          <button className="btn">+</button>
        </div>
        <button>Add to Cart </button>
      </div>
      <style jsx>{`
        .action {
          display: flex;
        }

        .qty-container {
          display: flex;
          height: 5rem;
        }

        .qty-input {
          width: 5rem;
          text-align: center;
          font-size: 1.6rem;
        }

        .btn {
          width: 5rem;
          border: 1px solid #e6e6e6;
        }
      `}</style>
    </>
  );
};

export default ProductAction;
