import React, { useState } from 'react';
import Layout from '../../components/Layout';
import axios from 'axios';
import baseURL from '../../utils/baseURL';

const Admin = () => {
  const initState = {
    name: '',
    price: '',
    description: '',
    category: ''
  };

  const [product, setProduct] = useState(initState);

  const [imagePreview, setImagePreview] = useState('');
  const [file, setFile] = useState('');

  const handleChange = e => {
    if (e.target.name === 'file') {
      let reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(event.target.files[0]);
      setFile(e.target.files[0]);
    } else {
      setProduct({
        ...product,
        [e.target.name]: e.target.value
      });
    }
  };

  const imageUpload = async () => {
    try {
      let data = new FormData();
      data.append('file', file);
      data.append('upload_preset', 'dress-shop');
      data.append('cloud_name', 'djlbfjouc');
      const res = await axios.post(process.env.CLOUDINARY_URL, data);
      return res.data.secure_url;
    } catch (error) {
      console.log(error.response);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      //upload image to cloudinary
      //get url of image
      const imageURL = await imageUpload();
      const payload = { ...product, imageURL };
      //save product to db
      await axios.post(`${baseURL}/api/products`, payload);
    } catch (error) {
      console.log(error);
    }
  };

  const reset = () => {
    setProduct({});
  };

  return (
    <Layout>
      <div className="container">
        <div className="page-title"> Add Product </div>
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div>
            <input
              type="text"
              name="name"
              placeholder="Product Name"
              onChange={handleChange}
            />
          </div>
          <div>
            <input type="file" name="file" onChange={handleChange} />
            {
              <div>
                {imagePreview && (
                  <img src={imagePreview} className="preview-img" />
                )}
              </div>
            }
          </div>
          <div>
            <input
              type="text"
              name="price"
              placeholder="Product Price"
              onChange={handleChange}
            />
          </div>
          <div>
            <input
              type="text"
              name="description"
              placeholder="Product Description"
              onChange={handleChange}
            />
          </div>
          <div>
            <select name="category" id="" onChange={handleChange}>
              <option value="men">Men</option>
              <option value="women">Women</option>
            </select>
          </div>
          <div>
            <button>Submit</button>
          </div>
        </form>
      </div>{' '}
      <style jsx>{`
        .container {
          max-width: 120rem;
          margin: 3rem auto;
        }
        .page-title {
          font-size: 3rem;
        }

        .preview-img {
          width: 100%;
        }
      `}</style>{' '}
    </Layout>
  );
};

export default Admin;
