const Filter = () => {
  return (
    <div className="filter-container">
      {' '}
      <span className="label"> Sort by </span>
      <div className="select">
        {' '}
        <span>Price</span>
        <ul className="dropdown">
          <li> Low to High </li>
          <li> High to Low </li>
        </ul>
      </div>
      <style jsx>{`
        .filter-container {
          display: flex;
        }

        .label {
          font-size: 1.6rem;
          padding-right: 1rem;
        }

        .select {
          border: 1px solid #eee;
          position: relative;
          font-size: 1.6rem;
          padding: 1rem 0;
          width: 20rem;
        }

        .dropdown {
          display: none;
          position: absolute;
        }
      `}</style>
    </div>
  );
};

export default Filter;
