const CartList = ({ carts }) => {
  console.log(carts);
  return (
    <div>
      {carts.length > 0 ? (
        carts.map(cart => (
          <div key={cart._id} className="product-list">
            <div className="product-img">
              <img src={cart.product.imageURL} alt="" />
            </div>
            <div className="product-info">
              <div className="product-name">{cart.product.name}</div>
              <div>
                <span className="product-qty">{cart.quantity}x</span>
                <span className="product-price">P{cart.product.price}</span>
              </div>
            </div>
          </div>
        ))
      ) : (
        <div> Your cart is empty :(</div>
      )}
      <style jsx>
        {`
          .product-list {
            display: flex;
            margin-top: 2rem;
          }

          .product-info {
            flex: 1;
            padding: 0 1rem;
          }

          .product-name {
            font-size: 2rem;
          }

          .product-img {
            width: 15rem;
            height: 15rem;
            overflow: hidden;
          }

          .product-img img {
            width: 100%;
            height: 100%;
            object-fit: contain;
          }

          .product-price {
            color: var(--color-primary);
            font-size: 1.8rem;
          }
        `}
      </style>
    </div>
  );
};

export default CartList;
