import { useCart } from "../../store/cart/cart.context";

const ProductAction = ({
  handleQty,
  qty,
  handleAddToCart,
  handleRemoveCart,
  product
}) => {
  const { carts } = useCart();

  console.log(product._id, carts);

  return (
    <>
      <div className="action">
        <div className="qty-container">
          <button className="btn" onClick={() => handleQty("sub")}>
            -
          </button>
          <input
            type="number"
            value="1"
            className="qty-input"
            value={qty}
            onChange={e => handleQty(e.target.value)}
          />
          <button className="btn" onClick={() => handleQty("add")}>
            +
          </button>
        </div>
        {carts.find(cart => cart.id === product._id) ? (
          <button className="btn-add-cart" onClick={handleRemoveCart}>
            Remove to Cart
          </button>
        ) : (
          <button className="btn-add-cart" onClick={handleAddToCart}>
            Add to Cart
          </button>
        )}
      </div>
      <style jsx>{`
        .action {
          display: flex;
          margin: 2rem 0;
        }

        .qty-container {
          display: flex;
          height: 5rem;
          border: 1px solid #e6e6e6;
          margin: 0 1rem;
        }

        .qty-input {
          width: 5rem;
          text-align: center;
          font-size: 1.6rem;
          border-left: 1px solid #e6e6e6;
          border-right: 1px solid #e6e6e6;
          border-top: 1px solid transparent;
          border-bottom: 1px solid transparent;
          background-color: #f7f7f7;
        }

        .btn {
          width: 5rem;
          border: 1px solid transparent;
          background-color: #fff;
        }

        .btn-add-cart {
          background-color: var(--color-primary);
          color: #fff;
          border: 1px solid var(--color-primary);
          font-size: 2rem;
          border-radius: 50px;
          width: 20rem;
          font-family: inherit;
          cursor: pointer;
        }

        .btn-add-cart:focus {
          outline: none;
        }
      `}</style>
    </>
  );
};

export default ProductAction;
