const SkeletonProduct = () => {
  return (
    <>
      <div className="skeleton-container">
        <div className="left">
          <div className="main-cover">
            <div className="box"></div>
          </div>
        </div>
        <div className="right">
          <div className="line"></div>
        </div>
      </div>
      <style jsx>{`
        .skeleton-container {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(500px, 1fr));
        }

        .main-cover {
          position: relative;
          padding-bottom: 100%;
          background-color: #eee;
        }

        .box {
          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          left: 0;
          object-fit: contain;
        }

        .right {
          padding: 1rem;
        }

        .line {
          height: 3rem;
          background-color: #eee;
          width: 100%;
        }
      `}</style>
    </>
  );
};

export default SkeletonProduct;
