import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import useModal from '../../hooks/useModal';
import Modal from '../../components/Shared/Modal';
import ProductForm from '../../components/Admin/Home/ProductForm';
import { IoMdAdd } from 'react-icons/io';
import ProductTable from '../../components/Admin/Home/ProductTable';
import axios from 'axios';
import baseURL from '../../utils/baseURL';

const Admin = () => {
  const { show, closeModal, openModal } = useModal();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function getProducts() {
      try {
        const { data } = await axios.get(`${baseURL}/api/products`);
        setProducts(data.products);
      } catch (error) {
        console.log(error);
      }
    }
    getProducts();
  }, []);

  async function handleDeleteProduct(id) {
    const filteredProducts = products.filter(product => product._id !== id);
    setProducts(filteredProducts);
  }

  return (
    <Layout>
      <div className="container">
        <Modal isOpen={show} close={closeModal} title={`Add Product`}>
          <ProductForm />
        </Modal>
        <div className="welcome-text">Hi, Welcome Back Admin</div>
        <div className="header">
          <div className="page-title"> List of All Products </div>
          <button onClick={openModal} className="btn">
            <span className="btn-text"> Add Product</span>
            <IoMdAdd />
          </button>
        </div>
        <ProductTable products={products} deleteProduct={handleDeleteProduct} />
      </div>{' '}
      <style jsx>{`
        .container {
          max-width: 120rem;
          margin: 3rem auto;
          padding: 0 2rem;
        }
        .page-title {
          font-size: 2.5rem;
        }

        .header {
          display: flex;
          justify-content: space-between;
        }

        .welcome-text {
          font-size: 2rem;
          padding-bottom: 2rem;
        }

        .btn {
          height: 5rem;
          padding: 0 2rem;
          background-color: var(--color-dark);
          color: #fff;
          font-size: 2rem;
          border: 1px solid transparent;
          cursor: pointer;
        }

        .btn-text {
          margin-right: 0.5rem;
        }
      `}</style>{' '}
    </Layout>
  );
};

export default Admin;
