import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { useCart } from "../store/cart/cart.context";
import Layout from "../components/Layout";
import axios from "axios";
import baseURL from "../utils/baseURL";
import ProductAction from "../components/Product/ProductAction";
import ProductInfo from "../components/Product/ProductInfo";

const Product = () => {
  const { id } = useRouter().query;
  const [loading, setLoading] = useState(true);
  const [product, setProduct] = useState("");
  const [qty, setQty] = useState(1);
  const { addCart, removeCart } = useCart();

  useEffect(() => {
    async function getProductInfo() {
      const payload = { params: { id } };
      const { data } = await axios.get(`${baseURL}/api/product`, payload);
      setProduct(data);
      setLoading(false);
    }
    getProductInfo();
  }, [id]);

  // handle change quantity
  const handleChangeQty = action => {
    if (action === "add") {
      setQty(qty => qty + 1);
    } else {
      if (qty > 1) setQty(qty => qty - 1);
    }
  };

  //handle add to cart
  const handleAddToCart = () => {
    const cartObj = { quantity: qty, id };
    addCart(cartObj);
  };

  return (
    <Layout>
      {loading ? (
        <div> Loading....</div>
      ) : (
        <div className="container">
          <div className="product-container">
            <div className="main">
              <div className="cover-img">
                <img className="img" src={product.imageURL} alt="" />
              </div>
            </div>
            <div className="product-info">
              <ProductInfo product={product} />
              <ProductAction
                handleQty={handleChangeQty}
                qty={qty}
                handleAddToCart={handleAddToCart}
                handleRemoveCart={() => removeCart(product.id)}
                product={product}
              />
            </div>
          </div>
        </div>
      )}
      <style jsx>{`
        .container {
          max-width: 120rem;
          margin: 2rem auto;
        }
        .product-container {
          display: flex;
          flex-wrap: wrap;
        }

        .main,
        .product-info {
          flex-basis: 50%;
        }

        .cover-img {
          position: relative;
          padding-bottom: 100%;
        }

        .img {
          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          left: 0;
          object-fit: contain;
        }
      `}</style>
    </Layout>
  );
};

export default Product;
