const Alert = ({ type, message }) => {
  return (
    <>
      <div className={`alert alert-${type}`}>{message}</div>
      <style jsx>
        {`
          .alert {
            padding: 1rem;
            border-radius: 6px;
          }

          .alert-error {
            color: #721c24;
            background-color: #f8d7da;
            border-color: #f5c6cb;
          }
        `}
      </style>
    </>
  );
};

export default Alert;
