import React, { createContext, useReducer, useContext } from 'react';
import reducer from './shopReducer';
import { LOAD_PRODUCTS } from './shopTypes';
import axios from 'axios';
import baseURL from '../../utils/baseURL';

const ShopContext = createContext();

const ShopProvider = ({ children }) => {
  const initState = {
    products: [],
    isLoading: true,
    currentPage: 1
  };

  const [state, dispatch] = useReducer(reducer, initState);

  async function loadProducts() {
    const payload = { params: { page: currentPage, limit: 5 } };
    const { data } = await axios.get(`${baseURL}/api/products`, payload);
    dispatch({ type: LOAD_PRODUCTS, payload: data.products });
  }

  return (
    <ShopContext.Provider value={{ products: state.products, loadProducts }}>
      {children}
    </ShopContext.Provider>
  );
};

const useShop = () => useContext(ShopContext);

export { ShopProvider, useShop };
