import upload from '../../middlewares/upload-image';

const uploadToS3 = (req, res) => {
  req.s3Key = uuid();
  const imageURL = `https://dress-shop.s3-ap-southeast-1.amazonaws.com/${req.s3Key}`;
  return new Promise((resolve, reject) => {
    return req.upload(req, res, err => {
      console.log(req.body);
    });
  });
};

const handle = async (req, res) => {
  uploadToS3(req, res);
  res.send('test');
};

export default upload(handle);
