const CartList = ({ carts }) => {
  return (
    <div>
      {carts.length > 0 ? (
        <>
          <div className="cart-header">
            <div className="cart-header-product"> Product </div>
            <div className="cart-header-lbl"> Quantity</div>
            <div className="cart-header-lbl"> Total</div>
          </div>
          {carts.map(cart => (
            <div key={cart._id} className="product-list">
              <div className="product">
                <div className="product-img">
                  <img src={cart.product.imageURL} alt="" />
                </div>
                <div>
                  <div className="product-name">{cart.product.name}</div>
                  <div className="product-price">P{cart.product.price}</div>
                </div>
              </div>
              <div className="product-qty product-content">
                {cart.quantity}{" "}
              </div>
              <div className="total product-content">
                P {cart.product.price * cart.quantity}
              </div>
            </div>
          ))}
        </>
      ) : (
        <div> Your cart is empty :(</div>
      )}
      }
      <style jsx>
        {`
          .cart-header {
            display: flex;
            font-size: 2rem;
            border-bottom: 1px solid #666;
            padding: 2rem 0;
            margin-bottom: 3rem;
          }

          .cart-header-product {
            width: 50%;
            text-align: left;
          }

          .cart-header-lbl {
            flex: 1;
            text-align: center;
          }

          .product-list {
            display: flex;
            margin-top: 2rem;
          }

          .product {
            display: flex;
            width: 50%;
          }

          .product-content {
            flex: 1;
            text-align: center;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .product-info {
            padding: 0 1rem;
          }

          .product-name {
            font-size: 2.5rem;
            font-weight: 600;
          }

          .product-img {
            width: 15rem;
            height: 15rem;
            overflow: hidden;
          }

          .product-img img {
            width: 100%;
            height: 100%;
            object-fit: contain;
          }

          .product-price {
            color: var(--color-primary);
            font-size: 1.8rem;
          }

          .product-qty {
            font-size: 2rem;
          }

          .total {
            font-size: 2rem;
          }
        `}
      </style>
    </div>
  );
};

export default CartList;
