const CartList = ({ carts }) => {
  return (
    <div>
      {carts.length > 0 ? (
        carts.map(cart => (
          <div key={cart._id} className="product-list">
            <div className="product">
              <div className="product-img">
                <img src={cart.product.imageURL} alt="" />
              </div>
              <div>
                <div className="product-name">{cart.product.name}</div>
                <div className="product-price">P{cart.product.price}</div>
              </div>
            </div>
            <div className="product-qty">{cart.quantity} </div>
            <div className="total">P{cart.product.price * cart.quantity}</div>
          </div>
        ))
      ) : (
        <div> Your cart is empty :(</div>
      )}
      <style jsx>
        {`
          .product-list {
            display: flex;
            margin-top: 2rem;
          }

          .product {
            display: flex;
          }

          .product-info {
            flex: 1;
            padding: 0 1rem;
          }

          .product-name {
            font-size: 2rem;
          }

          .product-img {
            width: 15rem;
            height: 15rem;
            overflow: hidden;
          }

          .product-img img {
            width: 100%;
            height: 100%;
            object-fit: contain;
          }

          .product-price {
            color: var(--color-primary);
            font-size: 1.8rem;
          }

          .product-qty {
            font-size: 1.6rem;
          }
        `}
      </style>
    </div>
  );
};

export default CartList;
