import React, { useState } from 'react';
import Layout from '../../components/Layout';
import axios from 'axios';
import baseURL from '../../utils/baseURL';
import Spinner from '../../components/Shared/Loader/Spinner';
import useModal from '../../hooks/useModal';
import Modal from '../../components/Shared/Modal';

const Admin = () => {
  const initState = {
    name: '',
    price: '',
    description: '',
    category: ''
  };

  const [product, setProduct] = useState(initState);
  const [imagePreview, setImagePreview] = useState('');
  const [file, setFile] = useState('');
  const [submit, setSubmit] = useState(false);

  const { show, closeModal, openModal } = useModal();

  const handleChange = e => {
    if (e.target.name === 'file') {
      let reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(event.target.files[0]);
      setFile(e.target.files[0]);
    } else {
      setProduct({
        ...product,
        [e.target.name]: e.target.value
      });
    }
  };

  const imageUpload = async () => {
    try {
      let data = new FormData();
      data.append('file', file);
      data.append('upload_preset', 'dress-shop');
      data.append('cloud_name', 'djlbfjouc');
      //upload image to cloudinary
      const res = await axios.post(process.env.CLOUDINARY_URL, data);
      return res.data.secure_url;
    } catch (error) {
      console.log(error.response);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      setSubmit(true);
      //get url of image
      const imageURL = await imageUpload();
      const payload = { ...product, imageURL };
      //save product to db
      await axios.post(`${baseURL}/api/products`, payload);
      reset();
    } catch (error) {
      console.log(error.response);
    } finally {
      setSubmit(false);
    }
  };

  const reset = () => {
    setProduct(initState);
    setFile('');
    setImagePreview('');
  };

  return (
    <Layout>
      <div className="container">
        <Modal isOpen={show} close={closeModal} title={`Add Product`}></Modal>
        <button onClick={openModal}> Add Product</button>
        <div className="page-title"> Add Product </div>
      </div>{' '}
      <style jsx>{`
        .container {
          max-width: 120rem;
          margin: 3rem auto;
          padding: 0 2rem;
        }
        .page-title {
          font-size: 3rem;
        }
      `}</style>{' '}
    </Layout>
  );
};

export default Admin;
