import React, { createContext, useReducer, useContext } from 'react';
import { LOGOUT_USER, SET_CURRENT_USER, SET_AUTH_ERROR } from './auth.types';
import axios from 'axios';
import cookie from 'js-cookie';
import Router from 'next/router';
import reducer from './auth.reducer';
import baseURL from '../../utils/baseURL';

const AuthContext = createContext();

const AuthProvider = ({ children, currentUser }) => {
  const initialState = {
    currentUser
  };

  const [state, dispatch] = useReducer(reducer, initialState);

  const login = async user => {
    try {
      const { data } = await axios.post(`${baseURL}/api/login`, user);
      const payload = { headers: { Authorization: data } };
      const response = await axios.get(`${baseURL}/api/account`, payload);
      dispatch({ type: SET_CURRENT_USER, payload: response.data });
    } catch (error) {
      dispatch({ type: SET_AUTH_ERROR, payload: error.response.data });
    }
  };

  const logout = () => {
    cookie.remove('token');
    window.localStorage.setItem('logout', Date.now());
    Router.push('/login');
    dispatch({ type: LOGOUT_USER });
  };

  return (
    <AuthContext.Provider
      value={{ currentUser: state.currentUser, logout, login }}
    >
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

export { AuthProvider, useAuth };
